      symbolZnacky: "▼",
      ukazatZnacky: false,
      karoserie: [],
      vybraneKaroserie: [],
      vsechnyKaroserie: true
    };
  },

  mounted() {
    axios.get('http://localhost:5000/api/posts/').then(response => {
      //console.log(response.data);
      this.Cars = response.data;
      this.getZnacky();
      this.getCaroseries();
