  async mounted() {
    try {
      this.Cars = await PostService.getPosts();
      this.getZnacky();
      this.getCaroseries();
      console.log('Data ze serveru nahrana');
    } catch (err) {
      this.error = err;
    }
  },

  methods: {
    odkrytZnacky() {
      this.ukazatZnacky = !this.ukazatZnacky;

      if (this.ukazatZnacky) {
        this.symbolZnacky = "▲";
      } else {
        this.symbolZnacky = "▼";
      }
    },

    odkrytKaroserie() {
      this.ukazatKaroserie = !this.ukazatKaroserie;

      if (this.ukazatKaroserie) {
        this.symbolKaroserie = "▲";
      } else {
        this.symbolKaroserie = "▼";
      }
    },

    omezeni() {
      if (this.minCena === null && this.maxCena === null && this.vsechnyZnacky && this.vsechnyKaroserie) {
        return true;
      } else {
        return false;
      }
    },

    //is selected car allready in array vybranaAuta
    isInArray(id) {
      let bool = false;

      for (let i = 0; i < this.vybranaAuta.length; i++) {
        if (this.vybranaAuta[i]._id === id) {
          bool = true;
        }
      }

      return bool;
    },

    seradit(parametr) {
      if (this.vybranaAuta.length === 0) {
        switch (parametr) {
          default:
            this.Cars.sort(function (a, b) {
              return a.cena - b.cena;
            });
            break;

          case 'nejdrazsi':
            this.Cars.sort(function (a, b) {
              return b.cena - a.cena;
            });
            break;

          case 'nejnovejsi':
            this.Cars.sort(function (a, b) {
              return b.rok - a.rok;
            });
            break;

          case 'kilometry':
            this.Cars.sort(function (a, b) {
              return a.kilometry - b.kilometry;
            });
            break;

          case 'vykon':
            this.Cars.sort(function (a, b) {
              return b.vykon - a.vykon;
            });
            break;
        }
      } else {
        switch (parametr) {
          default:
            this.vybranaAuta.sort(function (a, b) {
              return a.cena - b.cena;
            });
            break;

          case 'nejdrazsi':
            this.vybranaAuta.sort(function (a, b) {
              return b.cena - a.cena;
            });
            break;

          case 'nejnovejsi':
            this.vybranaAuta.sort(function (a, b) {
              return b.rok - a.rok;
            });
            break;

          case 'kilometry':
            this.vybranaAuta.sort(function (a, b) {
              return a.najeto - b.najeto;
            });
            break;

          case 'vykon':
            this.vybranaAuta.sort(function (a, b) {
              return b.vykon - a.vykon;
            });
            break;
        }
      }
    },

    vypisMinCenu(cena) {
      if (cena === '') {
        cena = 0;
      }

      if (cena !== null) {
        this.minCena = cena;

        if (this.vybranaAuta.length === 0) {
          this.vybranaAuta = this.Cars.filter(function (data) {
            return data.cena >= cena;
          });
        } else {
          this.filtruj();
        }
      }
    },

    vypisMaxCenu(cena) {
      if (cena === '') {
        cena = 10000000;
        this.$refs.maximalniCena.placeholder = '0';
      }

      if (cena !== null) {
        this.maxCena = cena;

        if (this.vybranaAuta.length === 0) {
          this.vybranaAuta = this.Cars.filter(function (data) {
            return data.cena <= cena;
          });
        } else {
          this.filtruj();
        }
      }
    },

    getZnacky() {
      this.vybraneZnacky = [];

      for (let i = 0; i < this.Cars.length; i++) {
        if (!this.znacky.includes(this.Cars[i].znacka)) this.znacky.push(this.Cars[i].znacka);
      }
    },

    getCaroseries() {
      this.vybraneKaroserie = [];

      for (let i = 0; i < this.Cars.length; i++) {
        if (!this.karoserie.includes(this.Cars[i].karoserie)) this.karoserie.push(this.Cars[i].karoserie);
      }
    },

    pridatZnacku(zn) {
      if (!this.vybraneZnacky.includes(zn) || this.vybraneZnacky.length === 0) {
        this.vybraneZnacky.push(zn);
        this.vsechnyZnacky = false;
      } else {
        //remove from vybranaKategorie
        this.vybraneZnacky.splice(this.vybraneZnacky.indexOf(zn), 1);

        if (this.vybraneZnacky.length === 0) {
          this.vsechnyZnacky = true;
        }
      }

      if (this.vybranaAuta.length === 0) {
        let znackyFiltru = this.vybraneZnacky;
        this.vybranaAuta = this.Cars.filter(function (data) {
          let bool = false;

          for (let i = 0; i < znackyFiltru.length; i++) {
            if (data.znacka === znackyFiltru[i]) {
              bool = true;
            }
          }

          return bool;
        });
      } else {
        for (let i = 0; i < this.Cars.length; i++) {
          if (this.vybraneZnacky.includes(this.Cars[i].znacka) && !this.isInArray(this.Cars[i]._id)) {
            this.vybranaAuta.push(this.Cars[i]);
          }
        }
      }

      this.filtruj();
      console.log('vybrane znacky', this.vybraneZnacky);
    },

    pridatKaroserii(karo) {
      if (!this.vybraneKaroserie.includes(karo) || this.vybraneKaroserie.length === 0) {
        this.vybraneKaroserie.push(karo);
        this.vsechnyKaroserie = false;
      } else {
        //remove from vybranaKategorie
        this.vybraneKaroserie.splice(this.vybraneKaroserie.indexOf(karo), 1);

        if (this.vybraneKaroserie.length === 0) {
          this.vsechnyKaroserie = true;
        }
      }

      if (this.vybranaAuta.length === 0) {
        let karoserieFiltru = this.vybraneKaroserie;
        this.vybranaAuta = this.Cars.filter(function (data) {
          let bool = false;

          for (let i = 0; i < karoserieFiltru.length; i++) {
            if (data.karoserie === karoserieFiltru[i]) {
              bool = true;
            }
          }

          return bool;
        });
      } else {
        for (let i = 0; i < this.Cars.length; i++) {
          if (this.vybraneKaroserie.includes(this.Cars[i].karoserie) && !this.isInArray(this.Cars[i]._id)) {
            this.vybranaAuta.push(this.Cars[i]);
          }
        } //this.filtruj();

      }

      this.filtruj();
      console.log('vybrane karoserie', this.vybraneKaroserie);
    },

    filtruj() {
      //this.vybranaAuta = this.Cars.filter(data => data.znacka === zn);
      let znackyF = this.vybraneZnacky;
      let karoserieF = this.vybraneKaroserie;

      if (this.vybraneZnacky.length !== 0) {
        this.vybranaAuta = this.Cars.filter(function (data) {
          let bool = false;

          for (let i = 0; i < znackyF.length; i++) {
            if (data.znacka === znackyF[i]) {
              bool = true;
            }
          }

          return bool;
        });
      } //2.filtr


      if (this.vybraneKaroserie.length !== 0) {
        this.vybranaAuta = this.vybranaAuta.filter(function (data) {
          let bool = false;

          for (let i = 0; i < karoserieF.length; i++) {
            if (data.karoserie === karoserieF[i]) {
              bool = true;
            }
          }

          return bool;
        });
      } //3.filtr


      if (this.minCena !== null && this.minCena !== '') {
        let cena = this.minCena;
        this.vybranaAuta = this.Cars.filter(function (data) {
          return data.cena >= cena;
        });
      } //4.filtr


      if (this.maxCena !== null && this.maxCena !== '') {
        let cena = this.maxCena;
        this.vybranaAuta = this.Cars.filter(function (data) {
          return data.cena <= cena;
        });
      }
    },

    toCarDetail(car) {
      this.choosenCar = car;
      this.oneCar = true;
    }

  }
};
