import { Bar } from 'vue-chartjs/legacy';
import { Chart as ChartJS, Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';
import axios from "axios";
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);
export default {
  name: "carAnalysis",
  components: {
    Bar
  },
  props: {
    chartId: {
      type: String,
      default: 'bar-chart'
    },
    datasetIdKey: {
      type: String,
      default: 'label'
    },
    width: {
      type: Number,
      default: 400
    },
    height: {
      type: Number,
      default: 400
    },
    cssClasses: {
      default: '',
      type: String
    },
    styles: {
      type: Object,
      default: () => {}
    },
    plugins: {
      type: Object,
      default: () => {}
    }
  },

  data() {
    return {
      sellsCars: {},
      chartData: {
        labels: ['Leden', 'Únor', 'Březen', 'Duben', 'Květen', 'Červen', 'Červenec', 'Září', 'Řijen', 'Listopad', 'Prosinec'],
        datasets: [{
          data: [40, 20, 12],
          backgroundColor: '#f87979'
        }]
      },
      chartOptions: {
        responsive: true
      }
    };
  },

  mounted() {
    axios.get('api/posts/').then(response => {
      //console.log(response.data);
      this.sellsCars = response.data;

      for (let i = 0; i < this.sellsCars.length; i++) {
        this.chartData.datasets[0].data[i] = this.sellsCars[i].vykon;
      }
    }).catch(error => {
      console.log(error);
    });
  },

  methods: {
    refreshData() {
      let array = [10, 8, 6, 4, 2];
      this.chartData.datasets[0].data = array;
      console.log(this.chartData.datasets.data);
    }

  }
};
